public class ScannerFor_1042 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner scan = new Scanner(System.in);
		
		int num = 0;
		String str = ""; 
		
		System.out.print("정수(숫자)를 입력해주세요 : ");
		num = scan.nextInt();
		
		for(int i=0; i<num ; i++) {
			str += "#";
		}
		
		System.out.println(str);
		scan.close();
		
	}

}
