package com.gsitm.java.ex04;

import java.util.Scanner;

/** 
* @과목명                : GS ITM 인턴사원 자바교육
* @FileName            : ScannerMoney_1064.java 
* @Project             : gsitm_java2 
* @Date                : 2018. 3. 28. 
* @작성자                : 장진영
* @프로그램 설명           : 키보드에서 정수로 된 돈의 액수를 입력바당 오만원권, 만원권, 천원권, 500원짜리 동전, 100원짜리 동전, 50원짜리 동전, 10원짜리동전,
*						 1원짜리 동전 각 몇개로 변환되는지 출력하다.
*/
public class ScannerMoney_1064 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner = new Scanner(System.in);
		int money = scanner.nextInt();
		
		
		System.out.print("오만원권 "+ money / 50000 + "개, ");
		money = money % 50000;
		if(money == 0) {
			return;
		}
		System.out.print("만원권 "+ money / 10000 + "개, ");
		money = money % 10000;
		if(money == 0) {
			return;
		}
		System.out.print("천원권 "+ money / 1000 + "개, ");
		money = money % 1000;
		if(money == 0) {
			return;
		}
		System.out.print("100원짜리 동전 "+ money / 100 + "개, ");
		money = money % 100;
		if(money == 0) {
			return;
		}
		System.out.print("50원짜리 동전 "+ money / 50 + "개,");
		money = money % 50;
		if(money == 0) {
			return;
		}
		System.out.print("10원짜리 동전  "+ money / 10 + "개,");
		money = money % 10;
		if(money == 0) {
			return;
		}
		System.out.print("1원짜리 동전  "+ money / 1 + "개,");
		money = money % 1;
		if(money == 0) {
			return;
		}

		
		
		
	}

}
