package com.gsitm.java.ex05;

public class intern_1034 {

	private String name = "김종주";
	private int age = 0;
	private String wannaSite = null;

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getAge() {
		return age;
	}

	public void setAge(int age) {
		this.age = age;
	}

	public String getWannaSite() {
		return wannaSite;
	}

	public void setWannaSite(String wannaSite) {
		this.wannaSite = wannaSite;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		intern_1034 intern = new intern_1034();

		System.out.println(intern.getName());

	}

	// 생성자
	intern_1034() {

	}

	intern_1034(String name) {
		this(name, 27);
	}

	intern_1034(String name, int age) {
		this(name, age, "SI도 가고싶고 집가까운곳도 가고싶음");
	}

	intern_1034(String name, int age, String wannaSite) {
		this.name = name;
		this.age = age;
		this.wannaSite = wannaSite;
	}
}
