public class BabyCat_1045 extends MomCat_1045{

	BabyCat_1045(String arm, String leg, String shoulder){
		super(arm, shoulder, leg);
	}
	@Override
	public String howl() {
		// TODO Auto-generated method stub
		super.howl();
		String howling = "음메~~";
		return howling;
	}

	public static void main(String[] args) {
		BabyCat_1045 cat = new BabyCat_1045("아기팔", "아기다리", "아기어깨");
		System.out.println(cat.howl());
		System.out.println(cat.leg);
		
		
	}
}
