public class Catling_1039 extends MotherCat_1039 {

	public Catling_1039( String arm, String leg, String shoulder) {
		super(arm, leg, shoulder);
		// TODO Auto-generated constructor stub
	}

	@Override
	public void cry() {
		// TODO Auto-generated method stub
		System.out.println("음메");
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MotherCat_1039 mother = new MotherCat_1039("엄마팔", "엄마다리", "엄마어깨");
		Catling_1039 young = new Catling_1039("새끼팔", "새끼다리", "새끼어깨");
		
		mother.setPurColor("회색");
		System.out.print("어미 고양이 울다 :");
		mother.cry();
		System.out.print("새끼 고양이 울다 :");
		young.cry();

	}
	
}
