public class ScannerFor_1064 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner = new Scanner(System.in);
		int inputInt = scanner.nextInt();  
		for(int i = 0; i<inputInt; i++) {
			System.out.print('#');
		}
	}

}
